import { Component, computed, effect, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet } from '@angular/router';

interface Task {
  name: string;
  isCompleted: boolean;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'signals-test';

  name = signal('Luis');

  tasks = signal<Task[]>([
    {
      name: 'ver curso signals',
      isCompleted: true
    }
  ])

  taskLenght = computed(()=> {
    return this.tasks().length;
  })

  constructor(){
    effect(()=> {
      if (this.tasks().length > 3) {
        console.log('tienes muchas tareas');
        alert('tienes muchas tareas')
      }
    })
  }
  toggleName(): void {
    this.name.set('Gabriel');
  }

  addRandomTask():void {
    this.tasks.update( (tasks) => {
      console.log(tasks);
      return [...tasks, {name: 'practicar signals', isCompleted: false}]
    })
  }

  markTaskAsCompleted(): void {
    this.tasks.mutate((tasks)=> {
      const taskToUpdate = this.tasks()[0];
      if (taskToUpdate) {
        taskToUpdate.name = 'angular 17'
        taskToUpdate.isCompleted = true;
      }
    })
  }


}
<h1>Signals!</h1>
<p>Mi nombre es: {{name()}}</p>
<button (click)="toggleName()">Cambiar nombre</button>

<ul>
  <li *ngFor="let task of tasks()">
  {{task.name}} - Completado: {{task.isCompleted ? 'Terminado': 'Pendiente'}}</li>
</ul>

<button (click)="addRandomTask()">Agregar random task</button>


<button (click)="markTaskAsCompleted()">Cambiar primera tarea</button>
<h2>Numero de tareas: {{taskLenght()}}</h2>
